const inputData = $input.first().json;
const htmlContent = inputData.data || inputData.body || '';
const sourceUrl = inputData.url || '';

if (!htmlContent) {
  return [];
}

const articles = [];

// Enhanced HTML parsing for Ghanaian news sites
let cleanText = htmlContent
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, '')
  .replace(/<footer[^>]*>[\s\S]*?<\/footer>/gi, '')
  .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, '');

// Extract article titles and content
const titleMatches = cleanText.match(/<h[1-6][^>]*>([^<]+)<\/h[1-6]>/gi) || [];
const contentMatches = cleanText.match(/<p[^>]*>([^<]{50,})<\/p>/gi) || [];

// Process potential articles
for (let i = 0; i < Math.min(titleMatches.length, 5); i++) {
  const title = titleMatches[i].replace(/<[^>]*>/g, '').trim();
  const content = contentMatches.slice(i, i + 3).map(p => p.replace(/<[^>]*>/g, '').trim()).join(' ');

  if (title.length > 10 && content.length > 200) {
    articles.push({
      original_title: title.substring(0, 200),
      original_content: content.substring(0, 1500),
      original_url: sourceUrl,
      source_name: sourceUrl.includes('myjoyonline') ? 'MyJoyOnline' : sourceUrl.includes('ghanaweb') ? 'GhanaWeb' : 'Web Source',
      source_url: sourceUrl,
      suggested_category: 'News',
      image_url: null
    });
  }
}

// Fallback: if no structured articles found, create one from all text
if (articles.length === 0 && cleanText.length > 500) {
  const fullText = cleanText.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
  if (fullText.length > 200) {
    articles.push({
      original_title: 'Web Article from ' + (sourceUrl.includes('myjoyonline') ? 'MyJoyOnline' : sourceUrl.includes('ghanaweb') ? 'GhanaWeb' : 'Web Source'),
      original_content: fullText.substring(0, 2000),
      original_url: sourceUrl,
      source_name: sourceUrl.includes('myjoyonline') ? 'MyJoyOnline' : sourceUrl.includes('ghanaweb') ? 'GhanaWeb' : 'Web Source',
      source_url: sourceUrl,
      suggested_category: 'News',
      image_url: null
    });
  }
}

return articles.slice(0, 3); // Limit to 3 articles per source

const items = $input.all();
const articles = [];

for (const item of items) {
  const data = item.json;

  const article = {
    original_title: data.title || '',
    original_content: (data.content || data.description || data.summary || '').replace(/<[^>]*>/g, '').trim(),
    original_url: data.link || data.url || '',
    source_name: 'RSS Source',
    source_url: data.link || data.url || '',
    suggested_category: data.category || 'News',
    image_url: null
  };

  if (article.original_title && article.original_content && article.original_content.length > 100) {
    if (article.original_content.length > 2000) {
      article.original_content = article.original_content.substring(0, 2000) + '...';
    }
    articles.push(article);
  }
}

return articles.slice(0, 10);

// Combine articles from both RSS and web scraping paths
const allArticles = [];

// Get all input items from all connected nodes
const items = $input.all();

for (const item of items) {
  if (item.json && typeof item.json === 'object') {
    // If it's an array of articles, spread them
    if (Array.isArray(item.json)) {
      allArticles.push(...item.json);
    } else {
      // If it's a single article object, add it
      allArticles.push(item.json);
    }
  }
}

console.log(`Combined ${allArticles.length} articles from all sources`);
return allArticles;

const results = $input.all();
let successful = 0;
let failed = 0;

for (const result of results) {
  if (result.json && result.json.success) {
    successful++;
  } else {
    failed++;
  }
}

console.log('Workflow completed:', successful, 'successful,', failed, 'failed submissions');

return [{
  successful_submissions: successful,
  failed_submissions: failed,
  total_processed: results.length,
  timestamp: new Date().toISOString()
}];
